import java.util.*;public class Main{public static void main(String[] args){Scanner in=new Scanner(System.in);int t=in.nextInt();for(int i=0;i<t;i++){String str=in.next();boolean len1=str.length()>1;int numb=len1?Integer.parseInt(str.charAt(str.length()-2)+""+str.charAt(str.length()-1)):Integer.parseInt(str);str=in.next();boolean len2=str.length()>1;int pow=len2?Integer.parseInt(str.charAt(str.length()-2)+""+str.charAt(str.length()-1)):Integer.parseInt(str);if(pow==0 && !len2){System.out.println("1");continue;}if(numb==0 && !len1){System.out.println("0");continue;}numb=numb%10;pow=pow%4;if(pow==0)pow=4;System.out.println((int)(Math.pow(numb,pow))%10);}}}